import React, { Component } from "react";

import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    };
  }
  componentDidMount() {
    fetch("https://api.tvmaze.com/search/shows?q=robin%20hood")
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      });
  }

  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    }
    return (
      <div className="App">
        <header className="App-header">
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <table>
            {items.map(item => (
              <tr>
                <td>
                  <a href="?query=item.show.id">{item.show.name}</a>
                </td>
              </tr>
            ))}
          </table>
        </header>
      </div>
    );
  }
}

export default App;
