import React from "react";

export class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false
        };
    }
    componentDidMount() {
        fetch("https://api.tvmaze.com/search/shows?q=robin%20hood")
            .then(res => res.json())
            .then(json => {
                console.log(json);
                this.setState({
                    isLoaded: true,
                    items: json
                });
            });
    }

    render() {
        var { isLoaded, items } = this.state;
        if (!isLoaded) {
            return <div>Loading...</div>;
        }
        return (
            <div className="App">
                <header className="App-header">
                    <table>
                        {this.state.items.map((item, key) => (
                            <tr>
                                <td>
                                    {item.show.id}
                                    <a href="#">{item.show.name}</a>
                                </td>
                            </tr>
                        ))}
                    </table>
                </header>
            </div>
        );
    }
}
